import React, { Component } from 'react';
import Spinner from 'react-bootstrap/Spinner';
import Nav from 'react-bootstrap/Nav'
import { Link } from 'react-router-dom';
import { clinvar } from '../services/clinvar.service';

class Clinvar extends Component {

    constructor(props) {
        super(props);

        this.state = {
            isShow: false,
            user: {},
            data: []
        };
    }

    componentDidMount() {
        this.setState({ 
            user: JSON.parse(localStorage.getItem('user')),
            data: { loading: true }
        });

        clinvar.getClinVar(this.props.geneID.text).then(data => {
            this.setState({ data })
        })
    }

    toggleShow = () => {
    this.setState(state => ({ isShow: !state.isShow }));
    };

    render() {
        const { data } = this.state;
        // Clinvar API Returns: AminoAcidChange,Chromosome,GeneSymbol,phenotype,NucleotideChange

    return (
        <div>
            <div style={{ justifyContent: "center"}}>
            <button onClick={this.toggleShow} type="button">
                Show Clinvar
            </button>
            </div>
        {this.state.isShow ? 
            <div>
                <h2>Clinvar Results</h2>
                {data[3].map((value, index) => (
                    <div>
                        <div>
                        Variant: {value[4]}
                        </div>
                        <div>
                        Amino Acid Change: {value[0]}
                        </div>
                        <div>
                        Phenotype: {value[3]}
                        </div>
                        <br></br>
                    </div>
                ))}
            </div>
            : 
            null
            }

        </div>
    );
    }
}

export { Clinvar };
