import React, { Component } from 'react';
import Spinner from 'react-bootstrap/Spinner';
import Nav from 'react-bootstrap/Nav'
import { Link } from 'react-router-dom';
import { clinvar } from '../services/clinvar.service';

class Clinvar extends Component {

    constructor(props) {
        super(props);

        this.state = {
            isShow: true,
            user: {},
            data: []
        };
    }

    componentDidMount() {
        this.setState({ 
            user: JSON.parse(localStorage.getItem('user')),
            data: { loading: true }
        });

        clinvar.getClinVar(JSON.stringify(this.props.geneID)).then(data => {
            this.setState({ data })
        })
    }

    toggleShow = () => {
    this.setState(state => ({ isShow: !state.isShow }));
    };

    render() {
        const { data } = this.state;

    return (
        <div>
        {this.state.isShow & !data.loading ? 
            <div>
                {JSON.stringify(data)}
            </div>
            : 
            <Spinner animation="border" role="status" variant="primary" size="lg">
            <span className="sr-only">Loading...</span>
            </Spinner>
            }

        <button onClick={this.toggleShow} type="button">
            Show Clinvar
        </button>
        </div>
    );
    }
}

export { Clinvar };
