import { userService } from '../services/user.service'

// Need to update to work with server.js responses!!
function handleResponse(response) {
    return response.text().then(text => {
        const data = text && JSON.parse(text);
        if (response.status !== 200) {
            if (response.status === 401) {
                // auto logout if 401 response returned from api
                userService.logout();
                window.location.reload(true);
            }

            const error = (data && data.message) || response.statusText;
            return Promise.reject(error);
        }

        return data;
    });
}

const apiUrl = "http://localhost:8002"

function getProbands() {
    const requestOptions = {
        method: 'GET',
        headers: { 'Content-Type': 'application/json',
                'Authorization': 'Basic ' + user.authdata, 
                },
    };

    return fetch(`${apiUrl}/probands`, requestOptions)
        .then(data => {
            if (data) {
                return data
            }
            return "Error";
        });
}
function getProbandById(proband) {
    const requestOptions = {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({data: "Request for list of probands and phenotypes"})
    };

    return fetch(`${apiUrl}/proband?id=${proband}`, requestOptions)
        .then(handleResponse)
        .then(data => {
            if (data) {
                return data
            }
            return "Error";
        });
}
function getPheno(proband) {
    const requestOptions = {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({data: "Request for list of probands and phenotypes"})
    };

    return fetch(`${apiUrl}/proband/genes?id=${proband}`, requestOptions)
        .then(handleResponse)
        .then(data => {
            if (data) {
                return data
            }
            return "Error";
        });
}
function getGenes(proband) {
    const requestOptions = {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({data: "Request for list of probands and phenotypes"})
