    return fetch(`${apiUrl}/proband/genes?id=${proband}`, requestOptions)
        .then(handleResponse)
        .then(data => {
            if (data) {
                return data
            }
            return "Error";
        });
}

export const api = {
    getProbands,
    getProbandById,
